        parah_19.add(new data_model_arabicandurdu("نَزَلَ بِهِ الرُّوحُ الْأَمِينُ", "اسے روح الامین (جبرائیل علیہ السلام) لے کر اترا ہے", "The Trusted Spirit (Gabriel) has descended with it,"));

