//                parah_29.add(new data_model_arabicandurdu("بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ", "اللہ کے نام سے شروع جو نہایت مہربان ہمیشہ رحم فرمانے والا ہے", "In the Name of Allah, Most Compassionate, Ever-Merciful."));

                parah_29.add(new data_model_arabicandurdu("بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ" +"\n\n"+"هَلْ أَتَى عَلَى الْإِنسَانِ حِينٌ مِّنَ الدَّهْرِ لَمْ يَكُن شَيْئًا مَّذْكُورًا" , "بے شک انسان پر زمانے کا ایک ایسا وقت بھی گزر چکا ہے کہ وہ کوئی قابلِ ذِکر چیز ہی نہ تھا", "Surely, there has passed over man a period of time when he was not a thing worth mentioning.",1));

                parah_29.add(new data_model_arabicandurdu("إِنَّا خَلَقْنَا الْإِنسَانَ مِن نُّطْفَةٍ أَمْشَاجٍ نَّبْتَلِيهِ فَجَعَلْنَاهُ سَمِيعًا بَصِيرًا", "بے شک ہم نے انسان کو مخلوط نطفہ سے پیدا فرمایا جسے ہم (تولّد تک ایک مرحلہ سے دوسرے مرحلہ کی طرف) پلٹتے اور جانچتے رہتے ہیں، پس ہم نے اسے (ترتیب سے) سننے والا (پھر) دیکھنے والا بنایا ہے", "Surely, We created man from a fertilized ovum that We keep turning and examining (from one stage to the next till birth). So We have made him (in the order of) hearing (then) seeing.",2));

                parah_29.add(new data_model_arabicandurdu("إِنَّا هَدَيْنَاهُ السَّبِيلَ إِمَّا شَاكِرًا وَإِمَّا كَفُورًا", "بے شک ہم نے اسے (حق و باطل میں تمیز کرنے کے لئے شعور و بصیرت کی) راہ بھی دکھا دی، (اب) خواہ وہ شکر گزار ہو جائے یا ناشکر گزار رہے", "Indeed, We also showed him the way (to understanding and insight for distinguishing between the truth and falsehood) whether he (now) becomes thankful or continues to be ungrateful.",3));

                parah_29.add(new data_model_arabicandurdu("إِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَلَاسِلَا وَأَغْلَالًا وَسَعِيرًا", "بیشک ہم نے کافروں کے لئے (پاؤں کی) زنجیریں اور (گردن کے) طوق اور (دوزخ کی) دہکتی آگ تیار کر رکھی ہے", "Verily, We have prepared for the disbelievers chains (for the feet), neck-fetters and the Blazing Fire (of Hell).",4));

                parah_29.add(new data_model_arabicandurdu("إِنَّ الْأَبْرَارَ يَشْرَبُونَ مِن كَأْسٍ كَانَ مِزَاجُهَا كَافُورًا", "بیشک مخلص اِطاعت گزار (شرابِ طہور کے) ایسے جام پئیں گے جس میں (خوشبو، رنگت اور لذت بڑھانے کے لئے) کافور کی آمیزش ہوگی", "Surely, the sincerely devoted and obedient will drink from the cups (of the holy drink) mixed with kafur (to enhance its aroma, colour and taste).",5));

                parah_29.add(new data_model_arabicandurdu("عَيْنًا يَشْرَبُ بِهَا عِبَادُ اللَّهِ يُفَجِّرُونَهَا تَفْجِيرًا", "(کافور جنت کا) ایک چشمہ ہے جس سے (خاص) بندگانِ خدا (یعنی اَولیاء اللہ) پیا کریں گے (اور) جہاں چاہیں گے (دوسروں کو پلانے کے لئے) اسے چھوٹی چھوٹی نہروں کی شکل میں بہا کر (بھی) لے جائیں گے", "(Kafur is) a spring (in Paradise) that (the privileged) servants of Allah (i.e., Awliya’ Allah) will drink from, and will (also) make it flow to wherever they will desire in the form of small brooks (to make others drink of it).",6));

                parah_29.add(new data_model_arabicandurdu("يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُ مُسْتَطِيرًا", "(یہ بندگانِ خاص وہ ہیں) جو (اپنی) نذریں پوری کرتے ہیں اور اس دن سے ڈرتے ہیں جس کی سختی خوب پھیل جانے والی ہے", "(These privileged servants of Allah are those) who fulfil their vows and keep fearing that Day whose severity spreads afar.",7));

                parah_29.add(new data_model_arabicandurdu("وَيُطْعِمُونَ الطَّعَامَ عَلَى حُبِّهِ مِسْكِينًا وَيَتِيمًا وَأَسِيرًا", "اور (اپنا) کھانا اللہ کی محبت میں (خود اس کی طلب و حاجت ہونے کے باوُجود اِیثاراً) محتاج کو اور یتیم کو اور قیدی کو کھلا دیتے ہیں", "And they give (their own) food, in deep love of Allah, to the needy, the orphan and prisoner (out of sacrifice, despite their own desire and need for it),",8));

                parah_29.add(new data_model_arabicandurdu("إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللَّهِ لَا نُرِيدُ مِنكُمْ جَزَاءً وَلَا شُكُورًا", "(اور کہتے ہیں کہ) ہم تو محض اللہ کی رضا کے لئے تمہیں کھلا رہے ہیں، نہ تم سے کسی بدلہ کے خواست گار ہیں اور نہ شکرگزاری کے (خواہش مند) ہیں", "(And say:) ‘We are feeding you only to please Allah. We do not seek any recompense from you nor (wish for) any thanks.",9));

                parah_29.add(new data_model_arabicandurdu("إِنَّا نَخَافُ مِن رَّبِّنَا يَوْمًا عَبُوسًا قَمْطَرِيرًا", "ہمیں تو اپنے ربّ سے اُس دن کا خوف رہتا ہے جو (چہروں کو) نہایت سیاہ (اور) بدنما کر دینے والا ہے", "We fear from our Lord the Day which will make (the faces) look very dark (and) ugly.’",10));

                parah_29.add(new data_model_arabicandurdu("فَوَقَاهُمُ اللَّهُ شَرَّ ذَلِكَ الْيَوْمِ وَلَقَّاهُمْ نَضْرَةً وَسُرُورًا", "پس اللہ انہیں (خوفِ اِلٰہی کے سبب سے) اس دن کی سختی سے بچا لے گا اور انہیں (چہروں پر) رونق و تازگی اور (دلوں میں) سرور و مسرّت بخشے گا", "So Allah will save them from the terror of that Day (for their fear of Allah) and will grant them freshness, bloom and blush (on their faces) and ecstasy and delight (in their hearts),",11));

                parah_29.add(new data_model_arabicandurdu("وَجَزَاهُم بِمَا صَبَرُوا جَنَّةً وَحَرِيرًا", "اور اِس بات کے عوض کہ انہوں نے صبر کیا ہے (رہنے کو) جنت اور (پہننے کو) ریشمی پوشاک عطا کرے گا", "And will bless them with Gardens (to live in) and silky clothes (to wear), a reward for their patience.",12));

                parah_29.add(new data_model_arabicandurdu("مُّتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ لَا يَرَوْنَ فِيهَا شَمْسًا وَلَا زَمْهَرِيرًا", "یہ لوگ اس میں تختوں پر تکیے لگائے بیٹھے ہوں گے، نہ وہاں دھوپ کی تپش پائیں گے اور نہ سردی کی شدّت", "Reclining in it against cushions on raised couches, they will find there neither intense cold nor heat.",13));

                parah_29.add(new data_model_arabicandurdu("وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًا", "اور (جنت کے درختوں کے) سائے ان پر جھک رہے ہوں گے اور ان کے (میووں کے) گچھے جھک کر لٹک رہے ہوں گے", "And the shades (of trees in Paradise) will close upon them, and the bunches of their (fruits) will be hanging low.",14));

                parah_29.add(new data_model_arabicandurdu("وَيُطَافُ عَلَيْهِم بِآنِيَةٍ مِّن فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَا", "اور (خُدام) ان کے گرد چاندی کے برتن اور (صاف ستھرے) شیشے کے گلاس لئے پھرتے ہوں گے", "And (the servants) will go round them with vessels of silver and (pure and clean) goblets of shining glass,",15));

                parah_29.add(new data_model_arabicandurdu("قَوَارِيرَاْ مِن فِضَّةٍ قَدَّرُوهَا تَقْدِيرًا", "(اور) شیشے بھی چاندی کے (بنے) ہوں گے جنہیں انہوں نے (ہر ایک کی طلب کے مطابق) ٹھیک ٹھیک اندازہ سے بھرا ہوگا", "And glasses made of silver which they will have filled with exact measures (according to everyone’s desire).",16));

                parah_29.add(new data_model_arabicandurdu("وَيُسْقَوْنَ فِيهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَبِيلًا", "اور انہیں وہاں (شرابِ طہور کے) ایسے جام پلائے جائیں گے جن میں زنجبیل کی آمیزش ہوگی", "And they will be made to drink there cups of (the holy drink) mixed with Zanjabil.",17));

                parah_29.add(new data_model_arabicandurdu("عَيْنًا فِيهَا تُسَمَّى سَلْسَبِيلًا", "(زنجبیل) اس (جنت) میں ایک ایسا چشمہ ہے جس کا نام ’سلسبیل‘ رکھا گیا ہے", "(Zanjabil is) a spring in this (Paradise) named Salsabil.",18));

