        ayat_11.add(new data_model_arabicandurdu("وَانتَظِرُوا إِنَّا مُنتَظِرُونَ", "اور تم (بھی) انتظار کرو ہم (بھی) منتظر ہیں", "And wait on; we (too) are waiting.’", 0));

