        ayat_43.add(new data_model_arabicandurdu("بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ", "اللہ کے نام سے شروع جو نہایت مہربان ہمیشہ رحم فرمانے والا ہے", "In the Name of Allah, Most Compassionate, Ever-Merciful.", 0));

