        ayat_43.add(new data_model_arabicandurdu("وَالْكِتَابِ الْمُبِينِ", "قسم ہے روشن کتاب کی", "By the illumined Book,", 0));

        ayat_43.add(new data_model_arabicandurdu("إِنَّا جَعَلْنَاهُ قُرْآنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُونَ", "بیشک ہم نے اسے عربی (زبان) کا قرآن بنایا ہے تاکہ تم لوگ سمجھ سکو", "Verily, We have made it, the Qur’an, in the Arabic (language) so that you may understand.", 0));

