        ayat_44.add(new data_model_arabicandurdu("وَإِنْ لَّمْ تُؤْمِنُوا لِي فَاعْتَزِلُونِ", "اور اگر تم مجھ پر ایمان نہیں لاتے تو مجھ سے کنارہ کش ہو جاؤ", "And if you do not believe in me, then keep away from me.’", 0));

