        ayat_53.add(new data_model_arabicandurdu("أَمْ لَمْ يُنَبَّأْ بِمَا فِي صُحُفِ مُوسَى", "کیا اُسے اُن (باتوں) کی خبر نہیں دی گئی جو موسٰی (علیہ السلام) کے صحیفوں میں (مذکور) تھیں", "Has he not been given the knowledge of (matters) mentioned in the scriptures of Musa (Moses),", 0));

        ayat_53.add(new data_model_arabicandurdu("وَإِبْرَاهِيمَ الَّذِي وَفَّى", "اور ابراہیم (علیہ السلام) کے (صحیفوں میں تھیں) جنہوں نے (اللہ کے ہر امر کو) بتمام و کمال پورا کیا", "And in the (scriptures) of Ibrahim (Abraham), who accomplished (all the commands of Allah) with perfection?", 0));

        ayat_53.add(new data_model_arabicandurdu("أَلَّا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَى", "کہ کوئی بوجھ اٹھانے والا کسی دوسرے (کے گناہوں) کا بوجھ نہیں اٹھائے گا", "That no bearer of a burden will carry the burden of (the sins of) another;", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَن لَّيْسَ لِلْإِنسَانِ إِلَّا مَا سَعَى", "اور یہ کہ انسان کو (عدل میں) وہی کچھ ملے گا جس کی اُس نے کوشش کی ہوگی (رہا فضل اس پر کسی کا حق نہیں وہ محض اللہ کی عطاء و رضا ہے جس پر جتنا چاہے کر دے)", "And that man (according to justice) will get only that for which he strives. (As for bounty, no one has any right to it. That is merely Allah’s bestowal and pleasure, granting as much as He wills to whom He pleases.)", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّ سَعْيَهُ سَوْفَ يُرَى", "اور یہ کہ اُس کی ہر کوشش عنقریب دکھا دی جائے گی (یعنی ظاہر کر دی جائے گی)", "And that all his endeavours will soon be shown (i.e., exposed).", 0));

        ayat_53.add(new data_model_arabicandurdu("ثُمَّ يُجْزَاهُ الْجَزَاءَ الْأَوْفَى", "پھر اُسے (اُس کی ہر کوشش کا) پورا پورا بدلہ دیا جائے گا", "Then he will be given full reward (for all his endeavours);", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّ إِلَى رَبِّكَ الْمُنتَهَى", "اور یہ کہ (بالآخر سب کو) آپ کے رب ہی کی طرف پہنچنا ہے", "And that (ultimately all) have to return to your Lord;", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّهُ هُوَ أَضْحَكَ وَأَبْكَى", "اور یہ کہ وہی (خوشی دے کر) ہنساتا ہے اور (غم دے کر) رُلاتا ہے", "And that He is the One Who makes one laugh (granting delight) and makes one weep (granting grief);", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّهُ هُوَ أَمَاتَ وَأَحْيَا", "اور یہ کہ وہی مارتا ہے اور جِلاتا ہے", "And that He is the One Who causes death and gives life;", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّهُ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنثَى", "اور یہ کہ اُسی نے نَر اور مادہ دو قِسموں کو پیدا کیا", "And that He is the One Who has created the two genders, male and female,", 0));

        ayat_53.add(new data_model_arabicandurdu("مِن نُّطْفَةٍ إِذَا تُمْنَى", "نطفہ (ایک تولیدی قطرہ) سے جبکہ وہ (رَحمِ مادہ میں) ٹپکایا جاتا ہے", "Out of a (sperm) drop when it is ejaculated (into the mother’s womb);", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّ عَلَيْهِ النَّشْأَةَ الْأُخْرَى", "اور یہ کہ (مرنے کے بعد) دوبارہ زندہ کرنا (بھی) اسی پر ہے", "And that it is upon Him alone to raise up alive (also after death);", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّهُ هُوَ أَغْنَى وَأَقْنَى", "اور یہ کہ وہی (بقدرِ ضرورت دے کر) غنی کر دیتا ہے اور وہی (ضرورت سے زائد دے کر) خزانے بھر دیتا ہے", "And that He is the One Who makes self-sufficient (by providing according to needs), and He is the One Who enriches treasures (by giving surplus to the needs);", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّهُ هُوَ رَبُّ الشِّعْرَى", "اور یہ کہ وہی شِعرٰی (ستارے) کا رب ہے (جس کی دورِ جاہلیت میں پوجا کی جاتی تھی)", "And that He alone is the Lord of Sirius (a star that was worshipped in the days of ignorance);", 0));

        ayat_53.add(new data_model_arabicandurdu("وَأَنَّهُ أَهْلَكَ عَادًا الْأُولَى", "اور یہ کہ اسی نے پہلی (قومِ) عاد کو ہلاک کیا", "And that He is the One Who destroyed the first (people of) ‘Ad,", 0));

        ayat_53.add(new data_model_arabicandurdu("وَثَمُودَاْ فَمَا أَبْقَى", "اور (قومِ) ثمود کو (بھی)، پھر (ان میں سے کسی کو) باقی نہ چھوڑا", "And (the people of) Thamud (as well). Then He did not spare (any of them) to survive.", 0));

